<script>
import { styler, spring, listen, pointer, value } from 'popmotion';

export default {
    name: 'Satelite',
    props: {
        width: {
            type: String,
            default: () => '600'
        }
    },
    mounted() {
        if (process.browser) {
            const satellite = document.querySelector('.satellite');
            const divStyler = styler(satellite);
            const satelliteXY = value({ x: 0, y: 0 }, divStyler.set);

            listen(satellite, 'mousedown touchstart')
                .start((e) => {
                    e.preventDefault();
                    pointer(satelliteXY.get()).start(satelliteXY);
                });

            listen(document, 'mouseup touchend')
                .start(() => {
                    spring({
                        from: satelliteXY.get(),
                        velocity: satelliteXY.getVelocity(),
                        to: { x: 0, y: 0 },
                        stiffness: 200,
                        // mass: 1,
                        // damping: 10
                    }).start(satelliteXY);
                });
        }
    }
};
</script>

<style scoped>
#satellite {
    cursor: -webkit-grab;
    cursor: grab;
}
</style>
